import React, { Component } from 'react';
import "../css/nav.css";
import Scrollspy from 'react-scrollspy'

class NavBar extends Component{
    constructor(props) {
        super(props);

        this.toggle = this.toggle.bind(this);
        this.state = {
          isOpen: false
        };
      }
      toggle() {
        this.setState({
          isOpen: !this.state.isOpen
        });
      }

      scrollFunction() {
        if (document.body.scrollTop > 80 || document.documentElement.scrollTop > 80) {
          document.getElementById("navdiv").style.backgroundColor = "rgb(169, 169, 197)";
        } else {
          document.getElementById("navdiv").style.backgroundColor = "rgb(169, 169, 197, 0)"
        }
      }

    render(){
        return(
            <div className="navdiv">
          <a className="title1" href="/">JEREMIAH HALL DUI SCHOOL</a>
              <Scrollspy items={ ['home','about', 'contact'] } currentClassName="is-current">
            <div className="navbar1">
                <a className="nav-link" href="#home">Home</a>
                <a className="nav-link" href="#about">About</a>
                <a className="nav-link" href="#contact">Contact</a>
            </div>
              </Scrollspy>
            </div>
        )
    }

}

export default NavBar;
